<node TEXT="arguments" ID="ID_530033710" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="are optional" ID="ID_528868428" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="The named parameters turn out to be more like guidelines than anything else. You can call a function without passing the parameters it expects, in which case they will be set to undefined." ID="ID_1732500095" CREATED="1277697954914" MODIFIED="1277697954914"/>
</node>
<node TEXT="the &quot;arguments&quot; argument" ID="ID_750694818" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="functions have access to an additional variable inside their body called arguments, which is an array-like object holding all of the values passed to the function." ID="ID_1469079234" CREATED="1277697954914" MODIFIED="1277697954914"/>
<node TEXT="function average() {" ID="ID_1385527044" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="var sum = 0;" ID="ID_1929149978" CREATED="1277697954914" MODIFIED="1277697954914"/>
<node TEXT="for (var i = 0, j = arguments.length; i &lt; j; i++) {" ID="ID_859448593" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="sum += arguments[i];" ID="ID_296297679" CREATED="1277697954914" MODIFIED="1277697954914"/>
</node>
<node TEXT="}" ID="ID_534145581" CREATED="1277697954914" MODIFIED="1277697954914"/>
<node TEXT="return sum / arguments.length;" ID="ID_23267297" CREATED="1277697954914" MODIFIED="1277697954914"/>
<node TEXT="}" ID="ID_1549552448" CREATED="1277697954914" MODIFIED="1277697954914"/>
</node>
</node>
</node>
<node TEXT="the &quot;apply&quot; method" ID="ID_1373116096" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="every function has one" ID="ID_538984631" CREATED="1277697954914" MODIFIED="1277697954914"/>
<node TEXT="lets the function be called on an arbitrary array of arguments" ID="ID_42402338" CREATED="1277697954914" MODIFIED="1277697954914"/>
<node TEXT="has two arguments" ID="ID_317218834" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="first = a reference for &quot;this&quot;" ID="ID_1065896825" CREATED="1277697954914" MODIFIED="1277697954914">
<node TEXT="see &quot;this&quot;, under &quot;custom objects&quot;" ID="ID_1773472585" CREATED="1277697954914" MODIFIED="1277697954914"/>
<node TEXT="if the function doesn&apos;t refer to &quot;this&quot;, then the first argument doesn&apos;t matter, and can be set to &quot;null&quot;" ID="ID_79464624" CREATED="1277697954914" MODIFIED="1277697954914"/>
</node>
<node TEXT="second = the array" ID="ID_1359546727" CREATED="1277697954914" MODIFIED="1277697954914"/>
</node>
<node TEXT="examples" ID="ID_1783750358" CREATED="1277697954914" MODIFIED="1277697954914">
