<node TEXT="function lastNameCaps() {" ID="ID_1381800013" CREATED="1277697954915" MODIFIED="1277697954915">
<node TEXT="return this.last.toUpperCase();" ID="ID_517737415" CREATED="1277697954915" MODIFIED="1277697954915"/>
