<node TEXT="}" ID="ID_1661001826" CREATED="1277697954915" MODIFIED="1277697954915"/>
<node TEXT="var s = new Person(&quot;Simon&quot;, &quot;Willison&quot;);" ID="ID_50592799" CREATED="1277697954915" MODIFIED="1277697954915"/>
<node TEXT="lastNameCaps.call(s);" ID="ID_255059176" CREATED="1277697954915" MODIFIED="1277697954915"/>
