<node TEXT="rename and case statement in SQL import" ID="ID_1228862618" CREATED="1362093762430" MODIFIED="1362093771128">
<node TEXT="internal to this step, the case statement uses &quot;rx_cost&quot;, the original name, to refer to what will be named &quot;cost_rx&quot; in the output of the step" ID="ID_1759417026" CREATED="1362093779517" MODIFIED="1362093836542"/>
<node TEXT="proc sql nowarn;&#xa;  create table outd.clm_cohort_all_rx_cost&#xa;    as select distinct&#xa;    a.patient_id&#xa;    ,a.rx_cost as cost_rx&#xa;    ,case&#xa;      when b.t2dm_rx_flag=1&#xa;        then a.rx_cost&#xa;        else 0&#xa;      end as cost_t2dm_rx&#xa;  from outd.clm_cohort_all_rx as a&#xa;    left join&#xa;    outd.diabetes_ndcs as b&#xa;    on a.ndc=b.ndcnum;&#xa;quit;" ID="ID_62430561" CREATED="1362093771718" MODIFIED="1362093772449"/>
</node>
<node TEXT="tricks" ID="ID_601994730" CREATED="1369156266086" MODIFIED="1369156267105">
<node TEXT="keep only rows in A that match multiple rows in B" ID="ID_1865135350" CREATED="1369096235680" MODIFIED="1369156270785">
<node TEXT="if" ID="ID_1795800703" CREATED="1369096250879" MODIFIED="1369156228066">
<node TEXT="E is an event data set with multiple dates (e.g. start, end)" ID="ID_861084191" CREATED="1369096264951" MODIFIED="1369096413782"/>
<node TEXT="P is a list of times someone was enrolled" ID="ID_1492192867" CREATED="1369096259254" MODIFIED="1369096328661"/>
</node>
<node TEXT="and we want" ID="ID_302494614" CREATED="1369156240760" MODIFIED="1369156242881">
<node TEXT="make sure they were enrolled at each of the time variables in C -- that each corresponds to an entry in P" ID="ID_305182094" CREATED="1369096390226" MODIFIED="1369156244915"/>
</node>
<node TEXT="then we can" ID="ID_1502168431" CREATED="1369096374628" MODIFIED="1369096387053">
<node TEXT="use mutiple inner joins from E to P" ID="ID_603477846" CREATED="1369096398203" MODIFIED="1369096418742"/>
<node TEXT="bringing in no additional variables from P to E" ID="ID_77416350" CREATED="1369096418947" MODIFIED="1369096444620"/>
<node TEXT="but subsetting to those rows in P that do what we want" ID="ID_1882546358" CREATED="1369096430217" MODIFIED="1369096457525"/>
</node>
